import React, { useState } from 'react';
import './DaycareRegiForm.css';
import { useNavigate } from 'react-router-dom';

function DaycareManagement() {
  const [values, setValues] = useState({
    dcName: '',
    dcLocation: '',
    dcPhone: '',
    dcEmail: '',
    openDays: '',
    openTimes: '',
    noOfStaffMembers: '',
    amenitiesOffered: '',
    safetyFeatures: '',
    userID: ''
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleInput = (event) => {
    const { name, value } = event.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const validateInputs = (values) => {
    let errors = {};
    if (!values.dcName) errors.dcName = 'Daycare Name is required';
    if (!values.dcEmail) errors.dcEmail = 'Daycare Email is required';
    if (!values.dcPhone) errors.dcPhone = 'Daycare Phone is required';
    if (!values.dcLocation) errors.dcLocation = 'Daycare Location is required';
    if (!values.userID) errors.userID = 'User ID is required';
    return errors;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const validationErrors = validateInputs(values);
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await fetch('http://localhost:8800/api/daycare/register', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(values)
        });
        const result = await response.json();
        alert(response.ok ? result.message || 'Daycare registered successfully' : result.error || 'This user ID is wrong or already assigned. Please sign up.');
        if (!response.ok) navigate('/signup');
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    if (!values.userID) {
      alert('User ID is required to update daycare');
      return;
    }
    try {
      const response = await fetch(`http://localhost:8800/api/daycare/update/${values.userID}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(values)
      });
      const result = await response.json();
      alert(response.ok ? 'Daycare updated successfully' : result.error);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDelete = async (event) => {
    event.preventDefault();
    if (!values.userID) {
      alert('User ID is required to delete daycare');
      return;
    }
    try {
      const response = await fetch(`http://localhost:8800/api/daycare/delete/${values.userID}`, {
        method: 'DELETE'
      });
      const result = await response.json();
      alert(response.ok ? 'Daycare deleted successfully' : result.error);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="dc-form-container">
      <h1 className="dc-welcome-message">Manage Your Daycare</h1>
      <div className="dc-registration-card">
        <div className="dc-registration-card-body">
          <h2 className="dc-regi-form-name">Daycare Registration</h2>
          <form onSubmit={handleSubmit}>
            {/* Registration Fields */}
            <input type="text" name="dcName" value={values.dcName} onChange={handleInput} placeholder="Enter Daycare Name" required />
            <input type="email" name="dcEmail" value={values.dcEmail} onChange={handleInput} placeholder="Enter Daycare Email" required />
            <input type="text" name="dcPhone" value={values.dcPhone} onChange={handleInput} placeholder="Enter Phone" required />
            <input type="text" name="dcLocation" value={values.dcLocation} onChange={handleInput} placeholder="Enter Location" required />
            <input type="text" name="userID" value={values.userID} onChange={handleInput} placeholder="Enter User ID" required />
            <button type="submit">Register</button>
          </form>

          <h2>Update Daycare</h2>
          <form onSubmit={handleUpdate}>
            {/* Update Fields */}
            <input type="text" name="userID" value={values.userID} onChange={handleInput} placeholder="Enter User ID to Update" required />
            <input type="text" name="dcName" value={values.dcName} onChange={handleInput} placeholder="Enter New Name" />
            <input type="email" name="dcEmail" value={values.dcEmail} onChange={handleInput} placeholder="Enter New Email" />
            <button type="submit">Update</button>
          </form>

          <h2>Delete Daycare</h2>
          <form onSubmit={handleDelete}>
            <input type="text" name="userID" value={values.userID} onChange={handleInput} placeholder="Enter User ID to Delete" required />
            <button type="submit">Delete</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default DaycareManagement;
