import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const PetProfile = () => {
  const { petID } = useParams();
  const [pet, setPet] = useState(null);
  const [adoptionForm, setAdoptionForm] = useState({ adoID: '', reason: '' });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState('');
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    axios.get(`/api/pets/${petID}`)
      .then(response => setPet(response.data))
      .catch(error => console.error('Error fetching pet details:', error));
  }, [petID]);

  const validateForm = () => {
    let errors = {};
    if (!adoptionForm.adoID) errors.adoID = 'Adopter ID is required';
    if (!adoptionForm.reason) errors.reason = 'Reason for adoption is required';
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      const response = await axios.post('/api/adopt', { petID, ...adoptionForm });
      setMessage(response.data.message);
      setAdoptionForm({ adoID: '', reason: '' });
      setErrors({});
    } catch (error) {
      setMessage('Failed to submit adoption request');
    }
  };

  return (
    <div>
      {pet ? (
        <div>
          <h2>{pet.name}</h2>
          <p>Breed: {pet.breed}</p>
          <p>Age: {pet.age} years</p>
          <button onClick={() => setShowForm(!showForm)}>
            {showForm ? 'Hide Adoption Form' : 'Request Adoption'}
          </button>
        </div>
      ) : (
        <p>Loading pet details...</p>
      )}

      {showForm && (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Adopter ID:</label>
            <input
              type="text"
              value={adoptionForm.adoID}
              onChange={(e) => setAdoptionForm({ ...adoptionForm, adoID: e.target.value })}
            />
            {errors.adoID && <p>{errors.adoID}</p>}
          </div>

          <div>
            <label>Reason for Adoption:</label>
            <textarea
              value={adoptionForm.reason}
              onChange={(e) => setAdoptionForm({ ...adoptionForm, reason: e.target.value })}
            />
            {errors.reason && <p>{errors.reason}</p>}
          </div>

          <button type="submit">Submit Request</button>
        </form>
      )}

      {message && <p>{message}</p>}
    </div>
  );
};

export default PetProfile;
