import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaBell, FaCheckCircle, FaTimesCircle, FaHourglassHalf } from "react-icons/fa";
import "./AdoptionStatus.css";

const AdoptionStatus = () => {
  const navigate = useNavigate();

  // Sample notification data (Replace with API data)
  const [notifications, setNotifications] = useState([
    { id: 1, petName: "Buddy", status: "Approved", time: "10 min ago" },
    { id: 2, petName: "Max", status: "Pending", time: "30 min ago" },
    { id: 3, petName: "Bella", status: "Rejected", time: "1 hour ago" },
  ]);

  // Function to render status icons
  const renderStatusIcon = (status) => {
    switch (status) {
      case "Approved":
        return <FaCheckCircle className="status-icon approved" />;
      case "Pending":
        return <FaHourglassHalf className="status-icon pending" />;
      case "Rejected":
        return <FaTimesCircle className="status-icon rejected" />;
      default:
        return status;
    }
  };

  // Function to mark all notifications as read (removes them)
  const markAllAsRead = () => {
    setNotifications([]);
  };

  return (
    <div className="notifications-container">
      <h2 className="header">
        <FaBell className="header-icon" /> Adoption Status Notifications
      </h2>
      <p className="subtext">Stay updated with real-time alerts.</p>

      <div className="notifications-list">
        {notifications.length > 0 ? (
          notifications.map((notification) => (
            <div key={notification.id} className="notification-item">
              {renderStatusIcon(notification.status)}
              <span className="notification-text">
                <strong>{notification.petName}</strong> adoption status: <b>{notification.status}</b>
              </span>
              <span className="time-text">{notification.time}</span>
            </div>
          ))
        ) : (
          <p className="no-notifications">No new notifications.</p>
        )}
      </div>

      <div className="buttons">
        <button className="view-all-btn" onClick={() => navigate("/adoption-notifications")}>
          View All
        </button>
        {notifications.length > 0 && (
          <button className="mark-read-btn" onClick={markAllAsRead}>
            Mark All as Read
          </button>
        )}
      </div>
    </div>
  );
};

export default AdoptionStatus;
