import React, { useState } from 'react';
import axios from 'axios';
import { FaSearch, FaUser, FaEnvelope, FaPhone, FaMapMarkerAlt, FaDog, FaInfoCircle } from 'react-icons/fa';
import './FindAdoptionRequests.css';

const FindAdoptionRequests = () => {
    const [userID, setUserID] = useState('');
    const [adoptionRequests, setAdoptionRequests] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleInputChange = (e) => {
        setUserID(e.target.value);
    };

    const fetchAdoptionRequests = async () => {
        if (!userID) {
            setError('Please enter a valid user ID.');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const response = await axios.get(`http://localhost:8800/api/pet/adoptionRequest/${userID}`);
            if (response.data.adoptionRequests.length > 0) {
                setAdoptionRequests(response.data.adoptionRequests);
            } else {
                setError('No adoption requests found for this distributor.');
                setAdoptionRequests([]);
            }
        } catch (err) {
            console.error('Error fetching adoption requests:', err);
            setError('Failed to retrieve adoption requests. Please try again later.');
            setAdoptionRequests([]);
        }

        setLoading(false);
    };

    return (
        <div className="find-adoption-requests-container">
            <div className="find-adoption-requests-card">
                <h2><FaSearch /> Find Adoption Requests</h2>
                <div className="find-adoption-requests-form-group">
                    <label htmlFor="userID"><FaUser /> Enter Your User ID:</label>
                    <input
                        type="text"
                        id="userID"
                        value={userID}
                        onChange={handleInputChange}
                        placeholder="Enter your User ID"
                    />
                    <button 
                        className="find-adoption-requests-button" 
                        onClick={fetchAdoptionRequests} 
                        disabled={loading}
                    >
                        {loading ? 'Loading...' : 'Find Requests'}
                    </button>
                </div>

                {error && <p className="find-adoption-requests-error"><FaInfoCircle /> {error}</p>}

                {adoptionRequests.length > 0 && (
                    <div className="find-adoption-requests">
                        <h3>Adoption Requests:</h3>
                        <div className="requestsList">
                            {adoptionRequests.map((request) => (
                                <div key={request.petID} className="requestCard">
                                    <h4><FaDog /> {request.petName} ({request.petBreed})</h4>
                                    <p><FaUser /> <strong>Adopter:</strong> {request.adoName}</p>
                                    <p><FaEnvelope /> <strong>Email:</strong> {request.adoEmail}</p>
                                    <p><FaPhone /> <strong>Phone:</strong> {request.adoPhone}</p>
                                    <p><FaMapMarkerAlt /> <strong>Location:</strong> {request.adoLocation}</p>
                                    <p><FaInfoCircle /> <strong>Reason:</strong> {request.reasonForAdoption}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default FindAdoptionRequests;
