import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUserEdit, FaTrashAlt, FaPlusCircle } from 'react-icons/fa';
import './AdopterRegiForm.css';

function AdopterRegiForm() {
  const [values, setValues] = useState({
    adoName: '',
    adoEmail: '',
    adoPhone: '',
    adoLocation: '',
    userID: ''
  });

  const [updateValues, setUpdateValues] = useState({
    userID: '',
    adoName: '',
    adoEmail: '',
    adoPhone: '',
    adoLocation: ''
  });

  const [deleteUserID, setDeleteUserID] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:8800/api/adopter/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(values)
      });
      const result = await response.json();
      if (response.ok) {
        alert(result.message || 'Adopter registered successfully');
      } else {
        alert(result.error || 'This user ID is wrong or already assigned. Please sign up.');
        navigate('/signup');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8800/api/adopter/update/${updateValues.userID}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateValues)
      });
      const result = await response.json();
      alert(response.ok ? 'Adopter updated successfully' : result.error || 'Failed to update adopter');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDelete = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8800/api/adopter/delete/${deleteUserID}`, {
        method: 'DELETE'
      });
      const result = await response.json();
      alert(response.ok ? 'Adopter deleted successfully' : result.error || 'Failed to delete adopter');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="adopter-management">
      <h1>Adopter Management</h1>

      <div className="form-section">
        <h2><FaPlusCircle /> Register Adopter</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="adoName" placeholder="Adopter Name" onChange={(e) => setValues({...values, adoName: e.target.value})} required />
          <input type="email" name="adoEmail" placeholder="Email" onChange={(e) => setValues({...values, adoEmail: e.target.value})} required />
          <input type="text" name="adoPhone" placeholder="Phone" onChange={(e) => setValues({...values, adoPhone: e.target.value})} required />
          <input type="text" name="adoLocation" placeholder="Location" onChange={(e) => setValues({...values, adoLocation: e.target.value})} required />
          <input type="text" name="userID" placeholder="User ID" onChange={(e) => setValues({...values, userID: e.target.value})} required />
          <button type="submit">Register</button>
        </form>
      </div>

      <div className="form-section">
        <h2><FaUserEdit /> Update Adopter</h2>
        <form onSubmit={handleUpdate}>
          <input type="text" name="userID" placeholder="User ID" onChange={(e) => setUpdateValues({...updateValues, userID: e.target.value})} required />
          <input type="text" name="adoName" placeholder="New Name" onChange={(e) => setUpdateValues({...updateValues, adoName: e.target.value})} />
          <input type="email" name="adoEmail" placeholder="New Email" onChange={(e) => setUpdateValues({...updateValues, adoEmail: e.target.value})} />
          <input type="text" name="adoPhone" placeholder="New Phone" onChange={(e) => setUpdateValues({...updateValues, adoPhone: e.target.value})} />
          <input type="text" name="adoLocation" placeholder="New Location" onChange={(e) => setUpdateValues({...updateValues, adoLocation: e.target.value})} />
          <button type="submit">Update</button>
        </form>
      </div>

      <div className="form-section">
        <h2><FaTrashAlt /> Delete Adopter</h2>
        <form onSubmit={handleDelete}>
          <input type="text" name="userID" placeholder="User ID" onChange={(e) => setDeleteUserID(e.target.value)} required />
          <button type="submit">Delete</button>
        </form>
      </div>
    </div>
  );
}

export default AdopterRegiForm;
