import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUserEdit, FaTrashAlt, FaPlusCircle } from 'react-icons/fa';
import './DaycareRegiForm.css';

function DaycareRegiForm() {
  const [values, setValues] = useState({
    dcName: '',
    dcLocation: '',
    dcPhone: '',
    dcEmail: '',
    openDays: '',
    openTimes: '',
    noOfStaffMembers: '',
    amenitiesOffered: '',
    safetyFeatures: '',
    userID: ''
  });

  const [updateValues, setUpdateValues] = useState(values);
  const [deleteUserID, setDeleteUserID] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:8800/api/daycare/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(values)
      });
      const result = await response.json();
      alert(response.ok ? 'Daycare registered successfully' : result.error || 'Registration failed');
      if (!response.ok) navigate('/signup');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8800/api/daycare/update/${updateValues.userID}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateValues)
      });
      alert(response.ok ? 'Daycare updated successfully' : 'Update failed');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDelete = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8800/api/daycare/delete/${deleteUserID}`, {
        method: 'DELETE'
      });
      alert(response.ok ? 'Daycare deleted successfully' : 'Deletion failed');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="daycare-management">
      <h1 className="daycare-title">Daycare Management</h1>

      <div className="form-section">
        <h2><FaPlusCircle /> Register Daycare</h2>
        <form onSubmit={handleSubmit}>
          {Object.keys(values).map((key) => (
            <input key={key} className="form-input" type="text" placeholder={key} onChange={(e) => setValues({...values, [key]: e.target.value})} required />
          ))}
          <button className="form-button" type="submit">Register</button>
        </form>
      </div>

      <div className="form-section">
        <h2><FaUserEdit /> Update Daycare</h2>
        <form onSubmit={handleUpdate}>
          {Object.keys(updateValues).map((key) => (
            <input key={key} className="form-input" type="text" placeholder={key} onChange={(e) => setUpdateValues({...updateValues, [key]: e.target.value})} />
          ))}
          <button className="form-button" type="submit">Update</button>
        </form>
      </div>

      <div className="form-section">
        <h2><FaTrashAlt /> Delete Daycare</h2>
        <form onSubmit={handleDelete}>
          <input className="form-input" type="text" placeholder="User ID" onChange={(e) => setDeleteUserID(e.target.value)} required />
          <button className="form-button" type="submit">Delete</button>
        </form>
      </div>
    </div>
  );
}

export default DaycareRegiForm;
