import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaEnvelope, FaPhone, FaMapMarkerAlt, FaIdCard, FaBriefcase, FaTransgender, FaCalendar, FaUserPlus, FaEdit, FaTrash } from 'react-icons/fa';
import './AdopterRegiForm.css';

function AdopterRegiForm() {
  const [values, setValues] = useState({
    adoName: '', adoEmail: '', adoPhone: '', adoLocation: '', adoNIC: '',
    adoAge: '', adoJob: '', adoGender: '', userID: '', adoID: ''
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const validationErrors = validateInputs(values);
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await fetch('http://localhost:8800/api/adopter/register', {
          method: 'POST', headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(values)
        });
        const result = await response.json();
        if (response.ok) {
          alert(result.message || 'Adopter registered successfully');
        } else {
          alert(result.error || 'This user ID is wrong or already assigned. Please sign up.');
          navigate('/signup');
        }
      } catch (error) { console.error('Error:', error); }
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8800/api/adopter/update/${values.adoID}`, {
        method: 'PUT', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(values)
      });
      const result = await response.json();
      alert(result.message || 'Adopter updated successfully');
    } catch (error) { console.error('Error:', error); }
  };

  const handleDelete = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8800/api/adopter/delete/${values.adoID}`, { method: 'DELETE' });
      const result = await response.json();
      alert(result.message || 'Adopter deleted successfully');
    } catch (error) { console.error('Error:', error); }
  };

  const handleInput = (event) => {
    const { name, value } = event.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const validateInputs = (values) => {
    let errors = {};
    if (!values.adoName) errors.adoName = 'Adopter Name is required';
    if (!values.adoEmail) errors.adoEmail = 'Adopter Email is required';
    if (!values.adoPhone) errors.adoPhone = 'Adopter Phone is required';
    if (!values.adoLocation) errors.adoLocation = 'Adopter Location is required';
    if (!values.adoNIC) errors.adoNIC = 'Adopter NIC is required';
    if (!values.adoAge) errors.adoAge = 'Adopter Age is required';
    if (!values.adoGender) errors.adoGender = 'Adopter Gender is required';
    if (!values.adoJob) errors.adoJob = 'Adopter Job is required';
    if (!values.userID) errors.userID = 'User ID is required';
    return errors;
  };

  return (
    <div className="ado-form-container">
      <h1 className="ado-welcome-message">Join Our Network</h1>
      <div className="ado-registration-card">
        <h2 className="ado-regi-form-name">Adopter Registration</h2>
        <form onSubmit={handleSubmit}>
          {['adoName', 'adoEmail', 'adoPhone', 'adoLocation', 'adoNIC', 'adoAge', 'adoJob', 'adoGender', 'userID'].map((field, index) => (
            <div className="ado-form-group" key={index}>
              <label htmlFor={field}>{field.replace('ado', 'Adopter ')}</label>
              <input type="text" name={field} value={values[field]} onChange={handleInput} required placeholder={`Enter ${field.replace('ado', '')}`} />
              {errors[field] && <span className="text-danger">{errors[field]}</span>}
            </div>
          ))}
          <button type="submit" className="ado-submit-btn"><FaUserPlus /> Register</button>
          <button onClick={handleUpdate} className="ado-update-btn"><FaEdit /> Update</button>
          <button onClick={handleDelete} className="ado-delete-btn"><FaTrash /> Delete</button>
        </form>
      </div>
    </div>
  );
}

export default AdopterRegiForm;
