import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import DaycareBooking from './DaycareBooking';
import { FaPhone, FaEnvelope, FaMapMarkerAlt, FaCalendarAlt, FaClock, FaUsers, FaShieldAlt, FaDog } from 'react-icons/fa';
import './Daycare.css';

const Daycare = () => {
  const { dcID } = useParams();
  const [daycare, setDaycare] = useState([]);
  const [activeBookingForm, setActiveBookingForm] = useState(null);
  const [searchCriteria, setSearchCriteria] = useState({
    dcName: '',
    dcLocation: '',
    openDays: '',
    openTimes: ''
  });

  useEffect(() => {
    fetchDaycareDetails();
  }, []);

  const fetchDaycareDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:8800/api/daycare/daycare`);
      setDaycare(response.data);
    } catch (error) {
      console.error('Error fetching daycare details:', error);
    }
  };

  const handleBookingButtonClick = (dcID) => {
    setActiveBookingForm((prevId) => (prevId === dcID ? null : dcID));
  };

  const handleSearchChange = (e) => {
    setSearchCriteria({ ...searchCriteria, [e.target.name]: e.target.value });
  };

  const filteredDaycare = daycare.filter((dc) =>
    (searchCriteria.dcLocation === '' || dc.dcLocation.toLowerCase().includes(searchCriteria.dcLocation.toLowerCase())) &&
    (searchCriteria.dcName === '' || dc.dcName.toLowerCase().includes(searchCriteria.dcName.toLowerCase())) &&
    (searchCriteria.openDays === '' || dc.openDays.toLowerCase().includes(searchCriteria.openDays.toLowerCase())) &&
    (searchCriteria.openTimes === '' || dc.openTimes.toLowerCase().includes(searchCriteria.openTimes.toLowerCase()))
  );

  return (
    <div className="daycare-profile-container">
      <div className="dc-hero-section">
        <div className="dc-welcome-message">
          <h1>Premium Pet Daycare Services</h1>
          <p>Find the perfect daycare for your beloved pets with top-notch amenities and safety features.</p>
        </div>
      </div>

      <div className="search-bar">
        <input type="text" name="dcLocation" placeholder="Search by Location" value={searchCriteria.dcLocation} onChange={handleSearchChange} />
        <input type="text" name="dcName" placeholder="Search by Name" value={searchCriteria.dcName} onChange={handleSearchChange} />
        <input type="text" name="openDays" placeholder="Search by Open Days" value={searchCriteria.openDays} onChange={handleSearchChange} />
        <input type="text" name="openTimes" placeholder="Search by Open Times" value={searchCriteria.openTimes} onChange={handleSearchChange} />
      </div>

      <div className="daycare-card-container">
        {filteredDaycare.length === 0 ? (
          <p>No daycare centers found.</p>
        ) : (
          <div className="daycare-cards">
            {filteredDaycare.map((dc) => (
              <div key={dc.dcID} className="daycare-card">
                <div className="daycare-details">
                  <h2>{dc.dcName}</h2>
                  <p><FaMapMarkerAlt /> {dc.dcLocation}</p>
                  <p><FaPhone /> {dc.dcPhone}</p>
                  <p><FaEnvelope /> {dc.dcEmail}</p>
                  <p><FaCalendarAlt /> Open Days: {dc.openDays}</p>
                  <p><FaClock /> Open Times: {dc.openTimes}</p>
                  <p><FaUsers /> Staff Members: {dc.noOfStaffMembers}</p>
                  <p><FaDog /> Amenities: {dc.amenitiesOffered}</p>
                  <p><FaShieldAlt /> Safety: {dc.safetyFeatures}</p>

                  <button className="btn btn-primary" onClick={() => handleBookingButtonClick(dc.dcID)}>
                    {activeBookingForm === dc.dcID ? 'Cancel Booking' : 'Book Now'}
                  </button>

                  {activeBookingForm === dc.dcID && <DaycareBooking daycareId={dc.dcID} />}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Daycare;
