import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useNavigate} from "react-router-dom";
import PanelCard from "../PanelCard";
import { FaPaw, FaHistory, FaUserEdit, FaClipboardList, FaBell } from "react-icons/fa";
import { Mail, Phone, MapPin, User, BriefcaseMedical} from 'lucide-react';
import "../css/AdopterPanel.css"; // Only contains styles for AdopterPanel

const AdopterPanel = (adopter) => {
  const navigate = useNavigate();

  const [adopterProfile, setAdopterProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAdopterProfile = async () => {
        try {
            const response = await axios.get(`http://localhost:8800/api/adopterPanel/get/:adoID/${adopter.adoID}`);
            setAdopterProfile(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching adopter profile:', error);
            setLoading(false);
        }
    };

    fetchAdopterProfile();
}, [adopter.adoID]);

  return (
    <div className="adopterPanel-section">
      <h1>Adopter Management Panel</h1>
      <p>Manage your adoption journey seamlessly.</p>
      
      <div className="adopterPanel-content">
              {/* Vet Cards */}
              <div className="adopter-card-container">
                  <div key={adopter.adoID} className="adopter-card">
                    
                    <div className="adopteravatar">
                      <User size={50} />
                    </div>
                    <div className="adopterdetails">
                    {loading ? (
                    <p>Loading adopter profile...</p>
                ) : adopterProfile ? (
                    <>
                      <h2>{adopter.adoName}</h2>
                      <p><BriefcaseMedical size={18} /> <strong>Adopter ID:</strong> {adopter.adoID}</p>
                      <p><Mail size={18} /> <strong>User ID:</strong> {adopter.userID}</p>
                      <p><Phone size={18} /> <strong>Name:</strong> {adopter.adoName}</p>
                      <p><MapPin size={18} /> <strong>NIC:</strong> {adopter.adoNIC}</p>
                      <p><BriefcaseMedical size={18} /> <strong>Age:</strong> {adopter.adoAge}</p>
                      <p><Mail size={18} /> <strong>Job:</strong> {adopter.adoJob}</p>
                      <p><Phone size={18} /> <strong>Gender:</strong> {adopter.adoGender}</p>
                      <p><MapPin size={18} /> <strong>Location:</strong> {adopter.adoLocation}</p>
                      <p><BriefcaseMedical size={18} /> <strong>Email:</strong> {adopter.adoEmail}</p>
                      <p><Mail size={18} /> <strong>Phone:</strong> {adopter.AdoPhone}</p>
                      <p><Phone size={18} /> <strong>Household Composition:</strong> {adopter.householdComposition}</p>
                      <p><MapPin size={18} /> <strong>Reason for Adoption:</strong> {adopter.reasonForAdoption}</p>
                    </>
                ):(<p>No Adopter profile available for this user.</p>)}
                    </div>
                  </div>
                
              </div>
        <PanelCard
          icon={<FaPaw size={50} color="#fff" />}
          title="View Available Pets"
          description="Browse pets ready for adoption."
          onClick={() => navigate("/adopt")}
        />

        <PanelCard
          icon={<FaHistory size={50} color="#fff" />}
          title="Adoption History"
          description="View your past adoption records."
          onClick={() => navigate("/adoptionHistory")}
        />

        <PanelCard
          icon={<FaUserEdit size={50} color="#fff" />}
          title="Manage Profile"
          description="Update your adopter profile."
          onClick={() => navigate("/adopterProfile/${userID}")}
        />

        <PanelCard
          icon={<FaClipboardList size={50} color="#fff" />}
          title="Track Adoption Requests"
          description="Check the status of your requests."
          onClick={() => navigate("/trackAdoptionRequests")}
        />

        <PanelCard
          icon={<FaBell size={50} color="#fff" />}
          title="Adoption Status Notifications"
          description="Stay updated with real-time alerts."
          onClick={() => navigate("/adoptionStatus")}
        />
      </div>
    </div>
  );
};

export default AdopterPanel;
