import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { FaCheckCircle, FaTimesCircle, FaClinicMedical, FaBookMedical, FaHome } from "react-icons/fa";
import styles from "./MyPets.css";

const MyPets = () => {
  const { userID } = useParams();
  const [pets, setPets] = useState([]);

  useEffect(() => {
    const fetchPets = async () => {
      try {
        const response = await axios.get(`http://localhost:8800/api/pet/myPets/${userID}`);
        setPets(response.data);
      } catch (error) {
        console.error("Error fetching pets:", error);
      }
    };

    fetchPets();
  }, [userID]);

  return (
    <div className={styles.myPetsContainer}>
      <h1>My Registered Pets</h1>
      <p>View all your registered pets along with their history and status.</p>
      
      <table className={styles.petsTable}>
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Breed</th>
            <th>Age</th>
            <th>Status</th>
            <th>Daycare</th>
            <th>Vet Visits</th>
            <th>Health Card</th>
          </tr>
        </thead>
        <tbody>
          {pets.map((pet) => (
            <tr key={pet.petID}>
              <td><img src={pet.image} alt={pet.name} className={styles.petImage} /></td>
              <td>{pet.name}</td>
              <td>{pet.breed}</td>
              <td>{pet.age} years</td>
              <td>
                {pet.status === "available" ? (
                  <FaCheckCircle color="green" />
                ) : (
                  <FaTimesCircle color="red" />
                )} {pet.status}
              </td>
              <td><FaHome /> {pet.daycare ? pet.daycare.name : "No record"}</td>
              <td><FaClinicMedical /> {pet.vetVisits.length}</td>
              <td><FaBookMedical /> {pet.healthCard ? "Available" : "Not available"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MyPets;
